static HAL_StatusTypeDef DMA_Start_From_Current_LUT(void)
{
    // 1) Stop DMA request from TIM2 CH1
    __HAL_TIM_DISABLE_DMA(&htim2, TIM_DMA_CC1);

    // 2) Abort current DMA stream (interrupt mode, as required)
    HAL_StatusTypeDef st = HAL_DMA_Abort_IT(&hdma_tim2_ch1);
    if (st == HAL_BUSY || st == HAL_OK) {
        // Optional: wait up to a short timeout for ABORT to complete.
        uint32_t t0 = HAL_GetTick();
        while (hdma_tim2_ch1.State != HAL_DMA_STATE_READY) {
            if ((HAL_GetTick() - t0) > 5U) break; // tiny guard
        }
    }

    // 3) Start DMA with new source buffer (current LUT)
    uint32_t dest = (uint32_t)&(TIM3->CCR3);
    st = HAL_DMA_Start_IT(&hdma_tim2_ch1, (uint32_t)g_wave_ptrs[g_wave_idx], dest, NS);
    if (st != HAL_OK) return st;

    // 4) Re-enable TIM2 CH1 DMA request
    __HAL_TIM_ENABLE_DMA(&htim2, TIM_DMA_CC1);

    return HAL_OK;
}

