void EXTI0_IRQHandler(void){

	// TODO: Debounce using HAL_GetTick()
	uint32_t now = HAL_GetTick();
	if ((now - g_last_press_ms) < DEBOUNCE_MS) {
		// Still bouncing; just clear the EXTI flag and exit
		HAL_GPIO_EXTI_IRQHandler(Button0_Pin);
	    return;
	}
	g_last_press_ms = now;

	// TODO: Disable DMA transfer and abort IT, then start DMA in IT mode with new LUT and re-enable transfer
	g_wave_idx++;
	if (g_wave_idx >= WAVE_COUNT) g_wave_idx = 0;
	// Stop DMA, switch source buffer, restart DMA
	if (DMA_Start_From_Current_LUT() != HAL_OK) {
		// If something went wrong, fail safe
		Error_Handler();
	}
	// Update LCD label
	lcd_show_current_wave();

	HAL_GPIO_EXTI_IRQHandler(Button0_Pin); // Clear interrupt flags
}
